<template>
  <div v-if="roomImage" class="image-container">
    <img :src="roomImage" alt="Room Image">
  </div>
  <div class="grid">
    <Selector
        current-floor="Bâtiment A"
        :min="valMin"
        :max="valMax"
        :real-min="realMin"
        :real-max="realMax"
        :unit="unit"
        @updateSelectedOption="updateSelectedOption"
    ></Selector>

    <svg :viewBox=svgViewBox>
      <g>
        <g v-for="(room, roomId) in roomData" :key="roomId" :id="roomId" :class="{ changeColor: true }"
           :style="{ fill: room.color }" @click="showRoomDetail(roomId)">
          <title>{{ roomId }}</title>
          <path v-for="(path, index) in room.path" :key="index" :id="'path' + roomId + '_' + index" :d="path" />
        </g>
      </g>
    </svg>

  </div>
  <RoomDetail v-if="roomName" :room="roomName" :key="roomName"></RoomDetail>
</template>

<script>
import Selector from "@/components/batiments/utils/selector.vue";
import RoomDetail from "@/components/roomDetail/roomDetail.vue";
import {ref} from "vue";
import loadApiConfig from "@/utils/api.js";



export default {
  name: "Etage",
  components: {Selector, RoomDetail},
  props: {
    svgViewBox: {
      type: String,
      required: true,
    }, roomData: {
      type: Object,
      required: true,
    },
    batiment: {
      type: String,
      required: true,
    },
    etage: {
      type: String,
      required: true,
    },
  },
  data() {
    return {
      roomName: null,
      valMin: 0,
      valMax: 100,
      realMin: 0,
      realMax: 100,
      unit: "°C",
      apiBaseUrl: "",
      selectedOption: "activity",
      sse: null,
      roomImage: null,
    };
  },
  async mounted() {
    this.apiBaseUrl = await loadApiConfig();

    this.sse = this.$sse.create({
      url: `${this.apiBaseUrl}/Events/Data/${this.batiment}/${this.etage}/`,
      format: 'json',
    })

    console.log(this.sse)

    this.sse.on('error', (e) => {
      console.error('lost connection or failed to parse!', e);
    });

    this.sse.on('message', (message, lastEventId) => {
      console.log("message", message);
      this.roomData[message.room].data = message.data;
      this.updateColors();
    });
    this.sse.connect()
        .then(sse => {

          // Unsubscribes from event-less messages after 7 seconds
          setTimeout(() => {
            this.sse.off();
          }, 7000);

          // Unsubscribes from chat messages after 14 seconds
          setTimeout(() => {
            this.sse.off();
          }, 14000);
        })
        .catch((err) => {
          // When this error is caught, it means the initial connection to the
          // events server failed.  No automatic attempts to reconnect will be made.
          console.error('Failed to connect to server', err);
        });

    await this.fetchAllRoomData();
  },
  methods:{
    updateSelectedOption(selected){
      this.selectedOption = selected;
      this.updateColors();
    },
    showRoomDetail(roomId){
      if (roomId === "B229") {
        this.roomImage = "data:image/jpeg;base64,"
      }else {
        this.roomImage = null;
      }
      this.roomName = roomId;
    },
    async fetchAllRoomData() {
      try {
        const response = await fetch(`${this.apiBaseUrl}/ByRoom/?last_data=1&depth=1`);
        console.log(`${this.apiBaseUrl}/ByRoom/?last_data=1&depth=1`)
        const roomsData = await response.json();

        for (const roomKey in this.roomData) {
          if (this.roomData.hasOwnProperty(roomKey)) {
            const roomId = roomKey;
            const roomInfo = roomsData.find(room => room.room === roomId);

            if (roomInfo) {
              this.roomData[roomId].data = {
                id: roomInfo.all_data[0].id,
                time: roomInfo.all_data[0].time,
                temperature: roomInfo.all_data[0].temperature,
                humidity: roomInfo.all_data[0].humidity,
                activity: roomInfo.all_data[0].activity,
                co2: roomInfo.all_data[0].co2,
                tvoc: roomInfo.all_data[0].tvoc,
                illuminance: roomInfo.all_data[0].illuminance,
                infrared: roomInfo.all_data[0].infrared,
                infrared_and_visible: roomInfo.all_data[0].infrared_and_visible,
                pressure: roomInfo.all_data[0].pressure,
                state: true,
                battery: roomInfo.sensor.batterylevel,
              };
            } else {
              // Gérer le cas où les données de la salle ne sont pas disponibles
              this.roomData[roomId].state = false;
            }
          }
        }

        this.updateColors();
      } catch (error) {
        console.error('Erreur lors de la récupération des données des salles.', error);
      }
    },
    updateColors(){
      for (const roomId in this.roomData) {
        if (this.roomData.hasOwnProperty(roomId) && this.roomData[roomId].state) {
          const metricValue = parseFloat(this.roomData[roomId]['data'][this.selectedOption]);

          if (!isNaN(metricValue)) {
            this.roomData[roomId].color = this.getColorForMetric(metricValue, this.selectedOption);
          }
        }
      }
    },
    getColorForMetric(value, option) {
      const getColor = (min, max, value) => {
        const normalizedValue = (value - min) / (max - min);
        const hue = (1 - normalizedValue) * 240; // Utilisez 120 pour des couleurs de bleu à rouge
        if (hue < 0) {
          return `hsl(0, 100%, 50%)`;
        }
        if (hue > 240) {
          return `hsl(240, 100%, 50%)`;
        }
        return `hsl(${hue}, 100%, 50%)`;
      }

      if (option === "temperature") {
        this.valMin = 14;
        this.valMax = 30;
        if (value > this.valMax) {
          this.realMax = value + 5;
        } else {
          this.realMax = 30;
        }
        if (value < this.valMin) {
          this.realMin = value - 5;
        } else {
          this.realMin = 14;
        }
        this.unit = "°C";
        return getColor(14, 30, value);
      }
      if (option == "humidity") {
        this.valMin = 40;
        this.valMax = 60;
        if (value > this.valMax) {
          this.realMax = value + 5;
        } else {
          this.realMax = 60;
        }

        if (value < this.valMin) {
          this.realMin = value - 5;
        } else {
          this.realMin = 40;
        }

        this.unit = "%";
        return getColor(40, 60, value);
      }
      if (option == "co2") {
        this.valMin = 0;
        this.valMax = 1500;
        if (value > this.valMax) {
          this.realMax = value + 5;
        } else {
          this.realMax = 1500;
        }
        if (value < this.valMin) {
          this.realMin = value - 5;
        } else {
          this.realMin = 0;
        }
        this.unit = "ppm";
        return getColor(0, 1500, value);
      }
      if (option == "activity") {
        this.valMin = 0;
        this.valMax = 500;
        if (value > this.valMax) {
          this.realMax = value + 5;
        } else {
          this.realMax = 500;
        }
        if (value < this.valMin) {
          this.realMin = value - 5;
        } else {
          this.realMin = 0;
        }
        this.unit = "";
        return getColor(0, 500, value);
      }
      if (option == "battery") {
        this.valMin = 0;
        this.valMax = 100;
        this.realMin = 0;
        this.realMax = 100;

        this.unit = "%";
        return `hsl(${value*1.2}, 100%, 50%)`;
      }
    }
  }
}
</script>


